package greatestCommonDivisorStrings;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

class GreatestCommonDivisorStrings {

	@Test
	void test() {
		assertEquals("ABC", Solution.gcdOfStrings("ABCABC", "ABC"));
		assertEquals("AB", Solution.gcdOfStrings("ABABAB", "ABAB"));
		assertEquals("", Solution.gcdOfStrings("LEET", "CODE"));
		assertEquals("C", Solution.gcdOfStrings("CCC", "CC"));
		assertEquals("", Solution.gcdOfStrings("ABCDE", "CDE"));
		assertEquals("", Solution.gcdOfStrings("CDE", "ABCDE"));
		assertEquals("", Solution.gcdOfStrings("AB", "ABCDE"));
		assertEquals("", Solution.gcdOfStrings("", "ABCDE"));
		assertEquals("", Solution.gcdOfStrings("CDE", ""));
		assertEquals("ABAB", Solution.gcdOfStrings("ABAB", "ABABABAB"));
	}

	@Test
	void testcase90() {
		String str1 = "";
		String str2 = "";
		String expected = "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA";
		assertEquals(expected, Solution.gcdOfStrings(str1, str2));
	}
}
